// Enhanced training data preparation with quality filtering
const rawData = $input.all().map(item => item.json);
console.log(`Processing ${rawData.length} raw interactions`);

// Quality filtering criteria
function filterQualityData(interactions) {
  return interactions.filter(interaction => {
    const feedback = interaction.user_feedback || {};
    const metrics = interaction.processing_metrics || {};

    // Quality criteria
    const hasPositiveFeedback = feedback.rating >= 4 || feedback.helpful === true;
    const hasGoodConfidence = metrics.stt_confidence >= 0.8;
    const hasReasonableLength = interaction.transcription && interaction.transcription.length >= 10;
    const hasValidResponse = interaction.llm_response && interaction.llm_response.length >= 10;
    const noErrors = !metrics.errors || metrics.errors.length === 0;

    return hasPositiveFeedback && hasGoodConfidence && hasReasonableLength && hasValidResponse && noErrors;
  });
}

// Extract conversation pairs
function extractTrainingPairs(interactions) {
  return interactions.map(interaction => {
    const userMessage = interaction.transcription;
    const assistantResponse = interaction.user_feedback?.corrected_response || interaction.llm_response;
    const context = interaction.processing_metrics?.context || '';

    return {
      messages: [
        {
          role: 'system',
          content: 'You are a helpful AI assistant specialized in voice interactions. Keep responses natural and conversational.'
        },
        {
          role: 'user', 
          content: userMessage
        },
        {
          role: 'assistant',
          content: assistantResponse
        }
      ],
      metadata: {
        interaction_id: interaction.id,
        confidence: interaction.processing_metrics?.stt_confidence,
        rating: interaction.user_feedback?.rating,
        language: interaction.processing_metrics?.language || 'en',
        timestamp: interaction.created_at
      }
    };
  });
}

// Group by language and quality
function groupAndBalance(trainingPairs) {
  const grouped = {};

  trainingPairs.forEach(pair => {
    const lang = pair.metadata.language;
    if (!grouped[lang]) grouped[lang] = [];
    grouped[lang].push(pair);
  });

  // Balance datasets - ensure we don't have too much of one language
  const maxPerLanguage = 1000;
  Object.keys(grouped).forEach(lang => {
    if (grouped[lang].length > maxPerLanguage) {
      // Sort by rating and take best examples
      grouped[lang].sort((a, b) => (b.metadata.rating || 0) - (a.metadata.rating || 0));
      grouped[lang] = grouped[lang].slice(0, maxPerLanguage);
    }
  });

  return grouped;
}

// Generate training dataset
const qualityData = filterQualityData(rawData);
const trainingPairs = extractTrainingPairs(qualityData);
const groupedData = groupAndBalance(trainingPairs);

// Flatten and shuffle
const allPairs = Object.values(groupedData).flat();
const shuffled = allPairs.sort(() => 0.5 - Math.random());

// Split train/validation
const splitIndex = Math.floor(shuffled.length * 0.85);
const trainData = shuffled.slice(0, splitIndex);
const validationData = shuffled.slice(splitIndex);

// Create dataset metadata
const datasetId = `voice_ai_${new Date().toISOString().split('T')[0].replace(/-/g, '')}`;
const datasetMetadata = {
  id: datasetId,
  created: new Date().toISOString(),
  source: 'voice_interactions_feedback',
  quality: {
    total_raw: rawData.length,
    quality_filtered: qualityData.length,
    final_training: trainData.length,
    final_validation: validationData.length,
    filter_rate: Math.round((qualityData.length / rawData.length) * 100)
  },
  languages: Object.keys(groupedData),
  distribution: Object.fromEntries(
    Object.entries(groupedData).map(([lang, data]) => [lang, data.length])
  )
};

console.log('Dataset prepared:', datasetMetadata);

return {
  json: {
    dataset: {
      metadata: datasetMetadata,
      train: trainData,
      validation: validationData
    },
    readyForTraining: trainData.length >= 50
  }
};

// Create fine-tuning configuration for Ollama
const dataset = $json.dataset;
const baseModel = 'llama3.2:8b';

// Advanced training configuration
const trainingConfig = {
  model: baseModel,
  dataset_id: dataset.metadata.id,
  training_data: dataset.train,
  validation_data: dataset.validation,
  hyperparameters: {
    learning_rate: 0.0001,
    batch_size: 4,
    epochs: 3,
    warmup_steps: Math.floor(dataset.train.length * 0.1),
    weight_decay: 0.01,
    gradient_accumulation_steps: 2,
    max_grad_norm: 1.0,
    lr_scheduler: 'cosine',
    save_steps: Math.floor(dataset.train.length / 4)
  },
  model_config: {
    context_length: 4096,
    rope_scaling: 1.0,
    attention_dropout: 0.1,
    hidden_dropout: 0.1
  },
  training_arguments: {
    fp16: true,
    dataloader_num_workers: 4,
    remove_unused_columns: false,
    logging_steps: 10,
    evaluation_strategy: 'steps',
    eval_steps: Math.floor(dataset.train.length / 8),
    save_strategy: 'steps',
    load_best_model_at_end: true,
    metric_for_best_model: 'eval_loss',
    greater_is_better: false
  },
  job_metadata: {
    job_id: `finetune_${dataset.metadata.id}`,
    created_at: new Date().toISOString(),
    languages: dataset.metadata.languages,
    data_quality: dataset.metadata.quality
  }
};

return {
  json: trainingConfig
};

