// Monitor training job progress
const activeJobs = $input.all().map(item => item.json);

if (activeJobs.length === 0) {
  return {
    json: {
      hasActiveJobs: false,
      message: 'No active training jobs to monitor'
    }
  };
}

const jobUpdates = [];

for (const job of activeJobs) {
  try {
    // Check job status via Ollama API
    const response = await fetch(`http://ollama:11434/api/fine-tune/status/${job.job_id}`);
    const status = await response.json();

    jobUpdates.push({
      job_id: job.job_id,
      current_status: status.status,
      progress: status.progress || 0,
      metrics: status.metrics || {},
      last_updated: new Date().toISOString(),
      needs_update: status.status !== job.job_status
    });
  } catch (error) {
    console.log(`Error checking job ${job.job_id}:`, error.message);
    jobUpdates.push({
      job_id: job.job_id,
      current_status: 'error',
      error: error.message,
      last_updated: new Date().toISOString(),
      needs_update: true
    });
  }
}

return {
  json: {
    hasActiveJobs: true,
    jobUpdates,
    completedJobs: jobUpdates.filter(j => j.current_status === 'completed'),
    failedJobs: jobUpdates.filter(j => j.current_status === 'failed' || j.current_status === 'error')
  }
};
